mapper.select:
  		SELECT *
		FROM (SELECT ROWNUM RN, A.*
		FROM (SELECT *
		FROM QABOARD where code=#{code} ORDER BY writeDate
		)A)
		WHERE RN BETWEEN #{startqa} AND #{endqa}

SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					SELECT * 
					FROM qaboard 
					ORDER BY indexnum DESC 
					) A
			)
WHERE RN BETWEEN #{start} AND #{end}
