mapper.select:
SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					SELECT * 
					FROM review 
					ORDER BY reviewnum DESC 
					) A
			)
WHERE RN BETWEEN #{start} AND #{end}

  		SELECT * 
			FROM (
				SELECT ROWNUM RN, A.* 
					FROM (
							SELECT * 
							FROM review 
							where ${search_key} like '%'||#{search}||'%' ORDER BY reviewnum DESC 
							) A
					)
		WHERE RN BETWEEN #{start} AND #{end}
